package com.java.Task;

public class NumSwapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=9;
		
		int b=12874;
		
//		int c;
//		
//		c=a;
//		
//		a=b;
//		
//		b=c;
		
		System.out.println("A="+a);
		
		System.out.println("B="+b);
		
		a=a+b;//15
		
		b=a-b;//15-5//10
		
		a=a-b;//15-10//5
		
		System.out.println("A="+a);
		
		System.out.println("B="+b);
	}

}
